import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { database } from '../firebase';
import { ref, get, update } from 'firebase/database';
import { useAuth } from '../contexts/AuthContext';

function QuestionDetail() {
  const { id } = useParams();
  const { currentUser } = useAuth();

  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!id) return;
    const questionRef = ref(database, `questions/${id}`);
    get(questionRef).then(snapshot => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        setQuestion({ id, ...data });
        setAnswers(data.answers || {});
      } else {
        setQuestion(null);
      }
      setLoading(false);
    });
  }, [id]);

  const handleVote = async (answerId, delta) => {
    if (!currentUser) {
      alert('Please log in to vote');
      return;
    }
    const answer = answers[answerId];
    if (!answer) return;

    // Prevent multiple votes by same user
    if (answer.voters && answer.voters[currentUser.uid]) {
      alert('You have already voted on this answer');
      return;
    }

    const answerRef = ref(database, `questions/${id}/answers/${answerId}`);
    const updatedVotes = (answer.votes || 0) + delta;
    const updatedVoters = { ...(answer.voters || {}) };
    updatedVoters[currentUser.uid] = true;

    try {
      await update(answerRef, {
        votes: updatedVotes,
        voters: updatedVoters,
      });
      setAnswers(prev => ({
        ...prev,
        [answerId]: {
          ...answer,
          votes: updatedVotes,
          voters: updatedVoters,
        }
      }));
    } catch (err) {
      alert('Error updating vote: ' + err.message);
    }
  };

  const handleAccept = async (answerId) => {
    if (!currentUser || currentUser.uid !== question.authorId) {
      alert('Only question owner can accept an answer');
      return;
    }
    const questionRef = ref(database, `questions/${id}`);
    try {
      await update(questionRef, { acceptedAnswerId: answerId });
      setQuestion(prev => ({ ...prev, acceptedAnswerId: answerId }));
    } catch (err) {
      alert('Error accepting answer: ' + err.message);
    }
  };

  if (loading) {
    return <p className="p-4 animate-pulse text-center">Loading question...</p>;
  }

  if (!question) {
    return <p className="p-4 text-center text-red-600">Question not found.</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-lg animate-fadeIn">
      <nav className="mb-4">
        <Link to="/" className="text-blue-600 hover:underline transition duration-300">
          {"< Back to Home"}
        </Link>
      </nav>
      <h1 className="text-4xl font-bold mb-4">{question.title}</h1>
      <div className="mb-6" dangerouslySetInnerHTML={{ __html: question.description }} />
      <div className="mb-6">
        {question.tags && question.tags.map((tag, idx) => (
          <span key={idx} className="inline-block bg-blue-200 text-blue-800 px-3 py-1 rounded-full mr-2 text-sm">
            {tag}
          </span>
        ))}
      </div>
      <h2 className="text-2xl font-semibold mb-4">Answers</h2>
      {Object.keys(answers).length === 0 && <p>No answers yet.</p>}
      <ul>
        {Object.entries(answers).map(([answerId, answer]) => (
          <li key={answerId} className={`mb-4 p-4 border rounded-lg ${question.acceptedAnswerId === answerId ? 'border-green-500 bg-green-50' : 'border-gray-300'}`}>
            <div dangerouslySetInnerHTML={{ __html: answer.content }} />
            <div className="mt-2 flex items-center justify-between">
              <div className="text-sm text-gray-600">By: {answer.authorName}</div>
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => handleVote(answerId, 1)}
                  className="text-green-600 hover:text-green-800"
                  title="Upvote"
                >
                  ▲ {answer.votes || 0}
                </button>
                <button
                  onClick={() => handleVote(answerId, -1)}
                  className="text-red-600 hover:text-red-800"
                  title="Downvote"
                >
                  ▼
                </button>
                {currentUser && currentUser.uid === question.authorId && question.acceptedAnswerId !== answerId && (
                  <button
                    onClick={() => handleAccept(answerId)}
                    className="text-blue-600 hover:underline"
                  >
                    Mark as Accepted
                  </button>
                )}
                {question.acceptedAnswerId === answerId && (
                  <span className="text-green-700 font-semibold">Accepted Answer</span>
                )}
              </div>
            </div>
          </li>
        ))}
      </ul>
      <Link
        to={`/answer/${id}`}
        className="inline-block mt-6 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-300"
      >
        Submit Your Answer
      </Link>
    </div>
  );
}

export default QuestionDetail;
