import React, { useEffect, useState } from 'react';
import { database } from '../firebase';
import { ref, onValue, query, orderByChild, limitToFirst, startAt } from 'firebase/database';
import { Link, useNavigate } from 'react-router-dom';

function Home() {
  const [questions, setQuestions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('newest');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const questionsRef = ref(database, 'questions');
    const q = query(questionsRef, orderByChild('createdAt'), limitToFirst(20));
    const unsubscribe = onValue(q, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const questionsArray = Object.entries(data).map(([id, value]) => ({
          id,
          ...value,
        }));
        setQuestions(questionsArray.reverse());
      } else {
        setQuestions([]);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const filteredQuestions = questions.filter(q =>
    q.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    q.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="container mx-auto p-4 max-w-4xl">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">StackIt</h1>
        <button
          onClick={() => navigate('/ask')}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Ask New Question
        </button>
      </div>
      <div className="mb-4 flex space-x-2">
        <input
          type="text"
          placeholder="Search questions..."
          className="flex-grow border border-gray-300 rounded px-3 py-2"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {/* Filters can be added here */}
      </div>
      {loading ? (
        <p>Loading questions...</p>
      ) : filteredQuestions.length === 0 ? (
        <p>No questions found.</p>
      ) : (
        <ul>
          {filteredQuestions.map((question) => (
            <li key={question.id} className="border-b border-gray-300 py-3">
              <Link to={`/question/${question.id}`} className="text-xl font-semibold text-blue-600 hover:underline">
                {question.title}
              </Link>
              <p className="text-gray-700 mt-1" dangerouslySetInnerHTML={{ __html: question.description.substring(0, 150) + '...' }} />
              <div className="mt-1">
                {question.tags.map((tag, idx) => (
                  <span key={idx} className="inline-block bg-gray-200 text-gray-800 text-xs px-2 py-1 rounded mr-2">
                    {tag}
                  </span>
                ))}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Home;
