import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import NotificationBell from './NotificationBell';

function Header() {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/');
    } catch (error) {
      alert('Failed to log out: ' + error.message);
    }
  };

  return (
    <header className="bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-50 animate-fadeIn">
      <Link to="/" className="text-3xl font-extrabold text-blue-600 hover:text-blue-700 transition duration-300">
        StackIt
      </Link>
      <nav className="space-x-6 text-gray-700 flex items-center">
        <Link to="/" className="hover:text-blue-600 transition duration-300 font-semibold">
          Home
        </Link>
        {currentUser && <NotificationBell />}
        {currentUser ? (
          <>
            <Link to="/ask" className="hover:text-blue-600 transition duration-300 font-semibold">
              Ask Question
            </Link>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-1 rounded shadow hover:bg-red-600 transition duration-300"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="hover:text-blue-600 transition duration-300 font-semibold">
              Login
            </Link>
            <Link to="/signup" className="hover:text-blue-600 transition duration-300 font-semibold">
              Sign Up
            </Link>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
