import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { database } from '../firebase';
import { ref, onValue, push, update, serverTimestamp } from 'firebase/database';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useAuth } from '../contexts/AuthContext';

const modules = {
  toolbar: [
    ['bold', 'italic', 'underline', 'strike'],
    [{ 'list': 'ordered'}, { 'list': 'bullet' }],
    ['emoji'],
    ['link', 'image'],
    [{ 'align': [] }],
    ['clean']
  ],
};

const formats = [
  'bold', 'italic', 'underline', 'strike',
  'list', 'bullet',
  'emoji',
  'link', 'image',
  'align'
];

function QuestionDetail() {
  const { id } = useParams();
  const { currentUser } = useAuth();

  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [newAnswer, setNewAnswer] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id) return;

    const questionRef = ref(database, `questions/${id}`);
    const unsubscribe = onValue(questionRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        setQuestion({ id, ...data });
        if (data.answers) {
          const answersArray = Object.entries(data.answers).map(([aid, a]) => ({
            id: aid,
            ...a,
          }));
          setAnswers(answersArray);
        } else {
          setAnswers([]);
        }
      } else {
        setQuestion(null);
        setAnswers([]);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, [id]);

  const handleSubmitAnswer = async (e) => {
    e.preventDefault();
    if (!newAnswer.trim()) {
      alert('Answer cannot be empty');
      return;
    }
    if (!currentUser) {
      alert('You must be logged in to submit an answer');
      return;
    }

    const answersRef = ref(database, `questions/${id}/answers`);
    const answerData = {
      content: newAnswer,
      authorId: currentUser.uid,
      authorName: currentUser.email,
      createdAt: serverTimestamp(),
      votes: 0,
      voters: {},
      comments: {},
    };

    try {
      await push(answersRef, answerData);
      setNewAnswer('');
    } catch (error) {
      alert('Error submitting answer: ' + error.message);
    }
  };

  const handleVote = async (answerId, voteType) => {
    if (!currentUser) {
      alert('You must be logged in to vote');
      return;
    }
    const answerRef = ref(database, `questions/${id}/answers/${answerId}`);
    const answer = answers.find(a => a.id === answerId);
    if (!answer) return;

    if (answer.voters && answer.voters[currentUser.uid]) {
      alert('You have already voted on this answer');
      return;
    }

    const updates = {};
    updates[`questions/${id}/answers/${answerId}/votes`] = (answer.votes || 0) + (voteType === 'up' ? 1 : -1);
    updates[`questions/${id}/answers/${answerId}/voters/${currentUser.uid}`] = true;

    try {
      await update(ref(database), updates);
    } catch (error) {
      alert('Error voting: ' + error.message);
    }
  };

  const handleAcceptAnswer = async (answerId) => {
    if (!currentUser) {
      alert('You must be logged in to accept an answer');
      return;
    }
    if (currentUser.uid !== question.authorId) {
      alert('Only the question owner can accept an answer');
      return;
    }
    try {
      await update(ref(database, `questions/${id}`), { acceptedAnswerId: answerId });
    } catch (error) {
      alert('Error accepting answer: ' + error.message);
    }
  };

  if (loading) {
    return <p className="p-4">Loading...</p>;
  }

  if (!question) {
    return <p className="p-4">Question not found.</p>;
  }

  return (
    <div className="container mx-auto p-6 max-w-4xl bg-white rounded-lg shadow-lg animate-fadeIn">
      <nav className="mb-6 text-blue-600 cursor-pointer" onClick={() => window.history.back()}>
        {"< Back to questions"}
      </nav>
      <h1 className="text-4xl font-extrabold mb-4">{question.title}</h1>
      <div className="mb-6">
        <div className="prose max-w-none" dangerouslySetInnerHTML={{ __html: question.description }} />
        <div className="mt-4">
          {question.tags && question.tags.map((tag, idx) => (
            <span key={idx} className="inline-block bg-blue-100 text-blue-800 text-xs px-3 py-1 rounded-full mr-2">
              {tag}
            </span>
          ))}
        </div>
      </div>
      <hr />
      <h2 className="text-3xl font-semibold mt-6 mb-4">Answers</h2>
      {answers.length === 0 && <p className="text-gray-600">No answers yet.</p>}
      <ul className="space-y-4">
        {answers.map((answer) => (
          <li key={answer.id} className={`border p-4 rounded-lg shadow ${question.acceptedAnswerId === answer.id ? 'border-green-500 bg-green-50 shadow-green-200' : 'border-gray-300'}`}>
            <div className="prose max-w-none" dangerouslySetInnerHTML={{ __html: answer.content }} />
            <div className="flex items-center mt-3 space-x-6">
              <div>
                <button
                  onClick={() => handleVote(answer.id, 'up')}
                  className="text-green-600 hover:text-green-800 mr-3"
                  title="Upvote"
                >
                  ▲
                </button>
                <button
                  onClick={() => handleVote(answer.id, 'down')}
                  className="text-red-600 hover:text-red-800"
                  title="Downvote"
                >
                  ▼
                </button>
                <span className="ml-2 font-semibold">{answer.votes || 0}</span>
              </div>
              {currentUser && currentUser.uid === question.authorId && question.acceptedAnswerId !== answer.id && (
                <button
                  onClick={() => handleAcceptAnswer(answer.id)}
                  className="text-blue-600 hover:underline font-semibold"
                >
                  Mark as Accepted
                </button>
              )}
              {question.acceptedAnswerId === answer.id && (
                <span className="text-green-600 font-semibold">Accepted Answer</span>
              )}
            </div>
          </li>
        ))}
      </ul>
      {currentUser ? (
        <div className="mt-8 bg-white p-6 rounded-lg shadow-lg">
          <textarea
            className="w-full border border-gray-300 rounded p-3 min-h-[120px] resize-y focus:outline-none focus:ring-4 focus:ring-blue-400 transition duration-300"
            placeholder="Write your answer here..."
            readOnly
          />
          <a
            href={`/answer/${question.id}`}
            className="inline-block mt-4 bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-6 py-3 rounded-lg shadow-lg hover:from-blue-700 hover:to-indigo-700 transition duration-300"
          >
            Submit Your Answer
          </a>
        </div>
      ) : (
        <p className="mt-8 text-gray-600">Please log in to submit an answer and vote.</p>
      )}
    </div>
  );
}

export default QuestionDetail;
