import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { database } from '../firebase';
import { ref, push, serverTimestamp } from 'firebase/database';
import { useAuth } from '../contexts/AuthContext';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const modules = {
  toolbar: [
    [{ 'header': [1, 2, false] }],
    ['bold', 'italic', 'strike'],
    [{ 'list': 'ordered'}, { 'list': 'bullet' }],
    ['emoji'],
    ['link', 'image'],
    [{ 'align': [] }],
    ['clean']
  ],
  clipboard: {
    matchVisual: false,
  }
};

const formats = [
  'header',
  'bold', 'italic', 'strike',
  'list', 'bullet',
  'emoji',
  'link', 'image',
  'align'
];

function AskQuestion() {
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [tags, setTags] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!title.trim()) {
      setError('Title is required');
      return;
    }
    if (!description.trim()) {
      setError('Description is required');
      return;
    }
    if (!tags.trim()) {
      setError('At least one tag is required');
      return;
    }
    if (!currentUser) {
      setError('You must be logged in to ask a question');
      return;
    }

    const questionData = {
      title,
      description,
      tags: tags.split(',').map(tag => tag.trim()),
      authorId: currentUser.uid,
      authorName: currentUser.email,
      createdAt: serverTimestamp(),
      answers: {},
      votes: 0,
      acceptedAnswerId: null,
    };

    try {
      const questionsRef = ref(database, 'questions');
      await push(questionsRef, questionData);
      alert('Question submitted successfully');
      navigate('/');
    } catch (error) {
      setError('Error submitting question: ' + error.message);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-lg animate-fadeIn">
      <h1 className="text-3xl font-semibold mb-6">Ask a New Question</h1>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="title" className="block text-lg font-medium text-gray-700 mb-1">Title</label>
          <input
            id="title"
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter a short and descriptive title"
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-4 focus:ring-blue-400 transition duration-300"
          />
        </div>
        <div>
          <label htmlFor="description" className="block text-lg font-medium text-gray-700 mb-1">Description</label>
          <ReactQuill
            id="description"
            value={description}
            onChange={setDescription}
            modules={modules}
            formats={formats}
            placeholder="Write a detailed description"
          />
        </div>
        <div>
          <label htmlFor="tags" className="block text-lg font-medium text-gray-700 mb-1">Tags (comma separated)</label>
          <input
            id="tags"
            type="text"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
            placeholder="e.g. React, JWT"
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-4 focus:ring-blue-400 transition duration-300"
          />
        </div>
        {error && <p className="text-red-600">{error}</p>}
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-3 rounded-lg shadow-lg hover:from-blue-600 hover:to-indigo-700 transition duration-300"
        >
          Submit Question
        </button>
      </form>
    </div>
  );
}

export default AskQuestion;
